import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, CircularProgress, Card, CardMedia, CardContent } from '@mui/material';

const API_URL = 'http://localhost:5000';

function ContentDetail() {
  const { id } = useParams();
  const [content, setContent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const res = await fetch(`${API_URL}/api/content/${id}`);
        if (res.ok) {
          const data = await res.json();
          setContent(data);
        } else {
          console.error("Failed to fetch content");
        }
      } catch (error) {
        console.error("Error fetching content:", error);
      }
      setLoading(false);
    };

    fetchContent();
  }, [id]);

  if (loading) {
    return <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}><CircularProgress /></Box>;
  }

  if (!content) {
    return <Typography variant="h5" sx={{ mt: 4, textAlign: 'center' }}>Content not found.</Typography>;
  }

  return (
    <Card sx={{ maxWidth: 900, margin: 'auto', mt: 4 }}>
      <CardMedia
        component={content.fileType === 'video' ? 'video' : 'img'}
        style={{ maxHeight: 600, objectFit: 'contain' }}
        image={`${API_URL}/${content.filePath}`}
        title={content.title}
        controls={content.fileType === 'video'}
        autoPlay
      />
      <CardContent>
        <Typography gutterBottom variant="h4" component="div">
          {content.title}
        </Typography>
        <Typography variant="body1" color="text.secondary" paragraph>
          {content.description}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Uploaded by: {content.uploader ? content.uploader.walletAddress : 'Unknown'}
        </Typography>
      </CardContent>
    </Card>
  );
}

export default ContentDetail;
